script:
frappe.ui.form.on('Training Event', {
	refresh: function() {
	    var url_id = frappe.model.get_value('YouTube Video', {'name': cur_frm.doc.youtube_video}, 'video_url');
	    var embed_string = '<h5>Video of Training</h5><iframe width="560" height="315" src="https://www.youtube.com/embed/' + url_id + '?cc_load_policy=1&modestbranding=1" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>';
        cur_frm.set_df_property('video_of_training', 'options', embed_string);
        cur_frm.refresh_field('video_of_training');
	}
});
