script:
frappe.ui.form.on('Training Event', {
	refresh: function(frm) {
        frappe.call({
				method: "frappe_youtube.api.get_youtube_iframe",
				args: {
					youtube_video: frm.doc.youtube_video
				},
				callback: function(r) {
                    cur_frm.set_df_property('video_of_training', 'options', '<h5>Video of Training</h5>' + r.message);
                    cur_frm.refresh_field('video_of_training');
				}
			});
	}
});
